mapper.select:
SELECT *
  FROM member
 WHERE mem_id = #{ userID }

mapper.update:
UPDATE member
   SET   mem_id = #{memId}
       , mem_pw = #{memPw}
       , mem_email = #{memEmail}
       , ch_date = now()
       , mem_name = #{memName}
       , mem_gen = #{memGen}
       , ch_id = #{chId}
 WHERE mem_id = #{memId}
