mapper.select:
SELECT *
  FROM member
 WHERE mem_id = #{ userID }

SELECT IFNULL(COUNT(1), 0)
  FROM member
 WHERE mem_id = #{ userID }

mapper.update:
UPDATE member
   SET   mem_id = #{memId}
       , mem_pw = #{memPw}
       , mem_email = #{memEmail}
       , ch_date = now()
       , mem_name = #{memName}
       , mem_gen = #{memGen}
 WHERE mem_id = #{memId}
