mapper.select:
SELECT * FROM product
WHERE product_name LIKE CONCAT('%', #{keyword} ,'%') OR product_regDate LIKE CONCAT('%', #{keyword}, '%') OR product_price LIKE CONCAT('%', #{keyword}, '%')
ORDER BY product_Id ASC, product_regDate DESC LIMIT #{paging.firstRecordOnPage}, #{paging.recordCountPerPage}

mapper.insert:
INSERT INTO product (
    product_Id
  , product_name
  , product_intro
  , product_price
  , product_regDate
  , file_seq
) 
VALUES ( 
  #{productId}
, #{productName}
, #{productIntro}
, #{productPrice}
, now()
, #{fileSeq}
)
