mapper.select:
  	SELECT COUNT(1)
	  FROM like_content
	 WHERE review_no = #{review_no}
  	   AND mem_no = #{mem_no}

SELECT like_check
  FROM like_content
 WHERE review_no = #{review_no}
   AND mem_no = #{mem_no}

mapper.update:
  	UPDATE like_content
  	  SET  like_check = #{like_switch}
  	 WHERE like_Id = (
  	 				  SELECT like_Id 
				  	  FROM like_content
				  	  WHERE review_no = #{review_no}
				  	    AND mem_no = #{mem_no}
				  	  )
