mapper.select:
 	 SELECT NVL(SUM(like_check), 0) AS like_count
 	   FROM like_content
	  WHERE review_no = #{ reviewNo }

  	SELECT COUNT(1)
	  FROM like_content
	 WHERE review_no = #{review_no}
  	   AND mem_no = #{mem_no}

SELECT IFNULL(MAX(like_check), 0) AS like_check
  FROM like_content
 WHERE review_no = #{review_no}
   AND mem_no = #{mem_no}

mapper.insert:
  	INSERT INTO review
	(review_title, review_writer, review_write_date, review_content, review_product_Id, review_ch_date 

	   , review_lock

	)
  	VALUES 
  	(
  	  #{reviewTitle}, #{reviewWriter}, now(), #{reviewContents}, #{reviewProductId}, now()

  	  , #{reviewLock}

  	)

mapper.update:
  	UPDATE like_content
  	  SET  like_check = #{like_switch}
  	 WHERE like_Id = (
  	 				  SELECT like_Id 
				  	  FROM like_content
				  	  WHERE review_no = #{review_no}
				  	    AND mem_no = #{mem_no}
				  	  )

UPDATE review
   SET view_count = view_count+1
 WHERE review_no = #{reviewNo}
